static inline void setDataPins(uint8_t value)
{
	D5_LOW; D7_LOW; D6_LOW; D4_LOW; D3_LOW; D2_LOW; D1_LOW; D0_LOW;

	if(value & 0x80) {
		D7_HIGH;
	}
	if(value & 0x40) {
		D6_HIGH;
	}
	if(value & 0x10) {
		D4_HIGH;
	}
	if(value & 0x08) {
		D3_HIGH;
	}
	if(value & 0x04) {
		D2_HIGH;
	}
	if(value & 0x02) {
		D1_HIGH;
	}
	if(value & 0x01) {
		D0_HIGH;
	}
	if(value & 0x20) {
		D5_HIGH;
	}
}

void print_logo()
{
	for(uint8_t row = 0; row < 16; ++row) {
		for(uint8_t col = 0; col < 240; ++col) {
			uint8_t data = 0;

			for(uint8_t i = 0; i < 8; ++i) {
				if(AGH_RT_LOGO[((row*8) + i) * 30 + col/8] & (0x80>>(col-((col/8)*8)))) {
					data |= (0x01<<i);
				}
			}

			lcd_data[row][col] = data;
		}
	}

	for(uint8_t row = 0; row < 16; ++row)
	{
		for(uint8_t col = 0; col < 240; ++col)
		{
			LCD_SendData(lcd_data[row][col]);
		}
	}
}

void LCD_Init()
{
	// Set bus mode - 8080/8-bit
	BM1_HIGH;
	BM0_LOW;

	// Set write/read
	WR0_HIGH;
	WR1_HIGH;

	// Enable chip select
	CS_HIGH;

	// Reset off
	RST_HIGH;

	// Set bias ratio
	LCD_SendCmd(LCD_CMD_SET_BR, LCD_CMD_SET_BR_m, 0b10);

	// Set gain and pot
	LCD_SendCmd(LCD_CMD_SET_GP, LCD_CMD_SET_GP_m, 0b10100000);

	// Set mirror y
	LCD_SendCmd(LCD_CMD_SET_MC, LCD_CMD_SET_MC_m, 0b1000);

	// Clear display data
	for(uint16_t i = 0; i < 3840; ++i)
	{
		LCD_SendData(0x00);
	}

	// Display enable
	LCD_SendCmd(LCD_CMD_DISP_EN, LCD_CMD_DISP_EN_m, 1);

	print_logo();
}

void LCD_SendCmd(uint8_t cmd, uint8_t mask, uint8_t val)
{
	CD_LOW;


	if(mask != 0xFF) {
		cmd = cmd & (~mask);
		val = val & mask;
		cmd = cmd | val;
	}

	setDataPins(cmd);

	WR0_LOW;
	asm volatile(
			"mov     r0, r0\n\t"
			"mov     r0, r0"
	);
	WR0_HIGH;

	if(mask == 0xFF) {
		LCD_SendCmd(val, 0, 0);
	}
}

void LCD_SendData(uint8_t data)
{
	CD_HIGH;

	setDataPins(data);

	WR0_LOW;
	asm volatile(
			"mov     r0, r0\n\t"
			"mov     r0, r0"
	);
	WR0_HIGH;

}
